changeSet.sql:
INSERT INTO roles (id, name)
SELECT *
FROM (SELECT 1, 'ROLE_USER') AS tmp
WHERE NOT EXISTS(
        SELECT name FROM roles WHERE name = 'ROLE_USER'
    ) LIMIT 1;

